 &dict-xsyn;
 &earthdistance;
 &file-fdw;
 &fuzzystrmatch;
 &hstore;
 &intagg;
 &intarray;
 &isn;
 &lo;
 <ree;
 &pageinspect;
 &passwordcheck;
